--with cross_items as (select i_item_sk ss_item_sk from tpcds.item, (select iss.i_brand_id brand_id, iss.i_class_id class_id, iss.i_category_id category_id from tpcds.store_sales, tpcds.item iss, tpcds.date_dim d1 where ss_item_sk = iss.i_item_sk and ss_sold_date_sk = d1.d_date_sk and d1.d_year between 1999 AND 1999 + 2 intersect select ics.i_brand_id, ics.i_class_id, ics.i_category_id from tpcds.catalog_sales, tpcds.item ics, tpcds.date_dim d2 where cs_item_sk = ics.i_item_sk and cs_sold_date_sk = d2.d_date_sk and d2.d_year between 1999 AND 1999 + 2 intersect select iws.i_brand_id, iws.i_class_id, iws.i_category_id from tpcds.web_sales, tpcds.item iws, tpcds.date_dim d3 where ws_item_sk = iws.i_item_sk and ws_sold_date_sk = d3.d_date_sk and d3.d_year between 1999 AND 1999 + 2) x where i_brand_id = brand_id and i_class_id = class_id and i_category_id = category_id ), avg_sales as (select avg(quantity*list_price) average_sales from (select ss_quantity quantity, ss_list_price list_price from tpcds.store_sales, tpcds.date_dim where ss_sold_date_sk = d_date_sk and d_year between 1999 and 2001 union all select cs_quantity quantity, cs_list_price list_price from tpcds.catalog_sales, tpcds.date_dim where cs_sold_date_sk = d_date_sk and d_year between 1998 and 1998 + 2 union all select ws_quantity quantity, ws_list_price list_price from tpcds.web_sales, tpcds.date_dim where ws_sold_date_sk = d_date_sk and d_year between 1998 and 1998 + 2) x) select channel, i_brand_id,i_class_id,i_category_id,sum(sales), sum(number_sales) from( select 'store' channel, i_brand_id,i_class_id, i_category_id,sum(ss_quantity*ss_list_price) sales, count(*) number_sales from tpcds.store_sales, tpcds.item, tpcds.date_dim where ss_item_sk in (select ss_item_sk from cross_items) and ss_item_sk = i_item_sk and ss_sold_date_sk = d_date_sk and d_year = 1998+2 and d_moy = 11 group by i_brand_id,i_class_id,i_category_id having sum(ss_quantity*ss_list_price) > (select average_sales from avg_sales) union al