with sr_items as (select i_item_id item_id, sum(sr_return_quantity) sr_item_qty from tpcds.store_returns, tpcds.item, tpcds.date_dim where sr_item_sk = i_item_sk and d_date in (select d_date from tpcds.date_dim where d_week_seq in (select d_week_seq from tpcds.date_dim where d_date in ('1998-03-21','1998-09-26','1998-11-15'))) and sr_returned_date_sk = d_date_sk group by i_item_id), cr_items as (select i_item_id item_id, sum(cr_return_quantity) cr_item_qty from tpcds.catalog_returns, tpcds.item, tpcds.date_dim where cr_item_sk = i_item_sk and d_date in (select d_date from tpcds.date_dim where d_week_seq in (select d_week_seq from tpcds.date_dim where d_date in ('1998-03-21','1998-09-26','1998-11-15'))) and cr_returned_date_sk = d_date_sk group by i_item_id), wr_items as (select i_item_id item_id, sum(wr_return_quantity) wr_item_qty from tpcds.web_returns, tpcds.item, tpcds.date_dim where wr_item_sk = i_item_sk and d_date in (select d_date from tpcds.date_dim where d_week_seq in (select d_week_seq from tpcds.date_dim where d_date in ('1998-03-21','1998-09-26','1998-11-15'))) and wr_returned_date_sk = d_date_sk group by i_item_id) select sr_items.item_id, sr_item_qty, sr_item_qty/(sr_item_qty+cr_item_qty+wr_item_qty)/3.0 * 100 sr_dev, cr_item_qty, cr_item_qty/(sr_item_qty+cr_item_qty+wr_item_qty)/3.0 * 100 cr_dev, wr_item_qty, wr_item_qty/(sr_item_qty+cr_item_qty+wr_item_qty)/3.0 * 100 wr_dev, (sr_item_qty+cr_item_qty+wr_item_qty)/3.0 average from sr_items, cr_items, wr_items where sr_items.item_id=cr_items.item_id and sr_items.item_id=wr_items.item_id order by sr_items.item_id, sr_item_qty fetch first 100 rows only;
select promotions,total,cast(promotions as decimal(15,4))/cast(total as decimal(15,4))*100 from (select sum(ss_ext_sales_price) promotions from tpcds.store_sales, tpcds.store, tpcds.promotion, tpcds.date_dim, tpcds.customer, tpcds.customer_address, tpcds.item where ss_sold_date_sk = d_date_sk and ss_store_sk = s_store_sk and ss_promo_sk = p_promo_sk and ss_customer_sk= c_customer_sk and ca_address_sk = c_current_addr_sk and ss_item_sk = i_item_sk and ca_gmt_offset = -6 and i_category = 'Books' and (p_channel_dmail = 'Y' or p_channel_email = 'Y' or p_channel_tv = 'Y') and s_gmt_offset = -6 and d_year = 1999 and d_moy = 12) promotional_sales, (select sum(ss_ext_sales_price) total from tpcds.store_sales, tpcds.store, tpcds.date_dim, tpcds.customer, tpcds.customer_address, tpcds.item where ss_sold_date_sk = d_date_sk and ss_store_sk = s_store_sk and ss_customer_sk= c_customer_sk and ca_address_sk = c_current_addr_sk and ss_item_sk = i_item_sk and ca_gmt_offset = -6 and i_category = 'Books' and s_gmt_offset = -6 and d_year = 1999 and d_moy = 12) all_sales order by promotions, total fetch first 100 rows only;
with ssr as (select s_store_id, sum(sales_price) as sales, sum(profit) as profit, sum(return_amt) as returns, sum(net_loss) as profit_loss from ( select ss_store_sk as store_sk, ss_sold_date_sk as date_sk, ss_ext_sales_price as sales_price, ss_net_profit as profit, cast(0 as decimal(7,2)) as return_amt, cast(0 as decimal(7,2)) as net_loss from tpcds.store_sales union all select sr_store_sk as store_sk, sr_returned_date_sk as date_sk, cast(0 as decimal(7,2)) as sales_price, cast(0 as decimal(7,2)) as profit, sr_return_amt as return_amt, sr_net_loss as net_loss from tpcds.store_returns ) salesreturns, tpcds.date_dim, tpcds.store where date_sk = d_date_sk and d_date between cast('1998-08-16' as date) and (cast('1998-08-16' as date) + 14 days) and store_sk = s_store_sk group by s_store_id), csr as (select cp_catalog_page_id, sum(sales_price) as sales, sum(profit) as profit, sum(return_amt) as returns, sum(net_loss) as profit_loss from ( select cs_catalog_page_sk as page_sk, cs_sold_date_sk as date_sk, cs_ext_sales_price as sales_price, cs_net_profit as profit, cast(0 as decimal(7,2)) as return_amt, cast(0 as decimal(7,2)) as net_loss from tpcds.catalog_sales union all select cr_catalog_page_sk as page_sk, cr_returned_date_sk as date_sk, cast(0 as decimal(7,2)) as sales_price, cast(0 as decimal(7,2)) as profit, cr_return_amount as return_amt, cr_net_loss as net_loss from tpcds.catalog_returns ) salesreturns, tpcds.date_dim, tpcds.catalog_page where date_sk = d_date_sk and d_date between cast('1998-08-16' as date) and (cast('1998-08-16' as date) + 14 days) and page_sk = cp_catalog_page_sk group by cp_catalog_page_id), wsr as (select web_site_id, sum(sales_price) as sales, sum(profit) as profit, sum(return_amt) as returns, sum(net_loss) as profit_loss from ( select ws_web_site_sk as wsr_web_site_sk, ws_sold_date_sk as date_sk, ws_ext_sales_price as sales_price, ws_net_profit as profit, cast(0 as decimal(7,2)) as return_amt, cast(0 as decimal(7,2)) as net_loss from tpcds.web_sales union all select ws_web_site_sk as wsr_web_site_sk, wr_returned_date_sk as date_sk, cast(0 as decimal(7,2)) as sales_price, cast(0 as decimal(7,2)) as profit, wr_return_amt as return_amt, wr_net_loss as net_loss from tpcds.web_returns left outer join tpcds.web_sales on ( wr_item_sk = ws_item_sk and wr_order_number = ws_order_number) ) salesreturns, tpcds.date_dim, tpcds.web_site where date_sk = d_date_sk and d_date between cast('1998-08-16' as date) and (cast('1998-08-16' as date) + 14 days) and wsr_web_site_sk = web_site_sk group by web_site_id) select channel, id, sum(sales) as sales, sum(returns) as returns, sum(profit) as profit from (select 'store channel' as channel, 'store' || s_store_id as id, sales, returns, (profit - profit_loss) as profit from ssr union all select 'catalog channel' as channel, 'catalog_page' || cp_catalog_page_id as id, sales, returns, (profit - profit_loss) as profit from csr union all select 'web channel' as channel, 'web_site' || web_site_id as id, sales, returns, (profit - profit_loss) as profit from wsr ) x group by rollup (channel, id) order by channel, id fetch first 100 rows only;
select channel, col_name, d_year, d_qoy, i_category, COUNT(*) sales_cnt, SUM(ext_sales_price) sales_amt FROM ( SELECT 'store' as channel, 'ss_addr_sk' col_name, d_year, d_qoy, i_category, ss_ext_sales_price ext_sales_price FROM tpcds.store_sales, tpcds.item, tpcds.date_dim WHERE ss_addr_sk IS NULL AND ss_sold_date_sk=d_date_sk AND ss_item_sk=i_item_sk UNION ALL SELECT 'web' as channel, 'ws_web_site_sk' col_name, d_year, d_qoy, i_category, ws_ext_sales_price ext_sales_price FROM tpcds.web_sales, tpcds.item, tpcds.date_dim WHERE ws_web_site_sk IS NULL AND ws_sold_date_sk=d_date_sk AND ws_item_sk=i_item_sk UNION ALL SELECT 'catalog' as channel, 'cs_ship_customer_sk' col_name, d_year, d_qoy, i_category, cs_ext_sales_price ext_sales_price FROM tpcds.catalog_sales, tpcds.item, tpcds.date_dim WHERE cs_ship_customer_sk IS NULL AND cs_sold_date_sk=d_date_sk AND cs_item_sk=i_item_sk) foo GROUP BY channel, col_name, d_year, d_qoy, i_category ORDER BY channel, col_name, d_year, d_qoy, i_category fetch first 100 rows only;
